@import "tailwindcss";

:root {
  --background: #ffffff;
  --foreground: #171717;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

@media (prefers-color-scheme: dark) {
  :root {
    --background: #0a0a0a;
    --foreground: #ededed;
  }
}

.text-primary {
  color: #1E1E1E;
}

.text-secondary {
  color: #1E1E1E;
  opacity: 0.75;
}

.text-tertiary {
  color: #1E1E1E;
  opacity: 0.5;
}
.text-quaternary {
  color: #1E1E1E;
  opacity: 0.25;
}

.poppins-thin {
  font-family: "Poppins", sans-serif;
  font-weight: 100;
  font-style: normal;
}

